function insert(){
	global $HTTP_SERVER_VARS, $HTTP_GET_VARS, $HTTP_POST_VARS, $HTTP_POST_FILES, $Translation;

	if($HTTP_GET_VARS['insert_x']){$HTTP_POST_VARS=$HTTP_GET_VARS;}

	// mm: can member insert record?
	$arrPerm=getTablePermissions('donations');
	if(!$arrPerm[1]){
		return 0;
	}

	$donation_soldfor = makeSafe($HTTP_POST_VARS["donation_soldfor"]);
	$donation_year = makeSafe($HTTP_POST_VARS["donation_year"]);
	$contact_id = makeSafe($HTTP_POST_VARS["contact_id"]);
	$contact_name_ignore = makeSafe($HTTP_POST_VARS["contact_name_ignore"]);
	$stockitem_id = makeSafe($HTTP_POST_VARS["stockitem_id"]);
	$donation_item = makeSafe($HTTP_POST_VARS["donation_item"]);
	$donation_radiotext_lookup = makeSafe($HTTP_POST_VARS["donation_radiotext_lookup"]);
	$donation_radiotext = makeSafe($HTTP_POST_VARS["donation_radiotext"]);
	$donation_msrp = makeSafe($HTTP_POST_VARS["donation_msrp"]);
	$donation_url = makeSafe($HTTP_POST_VARS["donation_url"]);
	$donation_image = PrepareUploadedFile('donation_image', 2000000,'jpg|jpeg|gif|png', false, "");
	if($donation_year== ""){
		echo StyleSheet() . "\n\n<div class=Error>" . $Translation["error:"] . " 'Auction Year': " . $Translation['field not null'] . "</div>";
		exit;
	}

	sql("insert into donations (donation_year, contact_id, stockitem_id, donation_item, donation_radiotext_lookup, donation_radiotext, donation_msrp, donation_image, donation_url) values (" . (($donation_year != "") ? "'$donation_year'" : "NULL") . ", " . (($contact_id != "") ? "'$contact_id'" : "NULL") . ", " . (($stockitem_id != "") ? "'$stockitem_id'" : "NULL") . ", " . (($donation_item != "") ? "'$donation_item'" : "NULL") . ", " . (($donation_radiotext_lookup != "") ? "'$donation_radiotext_lookup'" : "NULL") . ", " . (($donation_radiotext != "") ? "'$donation_radiotext'" : "NULL") . ", " . (($donation_msrp != "") ? "'$donation_msrp'" : "NULL") . ", " . (($donation_image != "") ? "'$donation_image'" : "NULL") . ", " . (($donation_url != "") ? "'$donation_url'" : "NULL") . ")");
	// mm: save ownership data
	$recID=mysql_insert_id();
	$res = sql("select contact_name from contacts where id='".makeSafe($contact_id)."' limit 1");
	if($row=@mysql_fetch_row($res)){
		if($row[0]!=''){
			sql("update donations set contact_name_ignore='$row[0]' where id='".makeSafe($recID)."'");
		}
	}
	
	sql("insert into membership_userrecords set tableName='donations', pkValue='$recID', memberID='".getLoggedMemberID()."', dateAdded='".time()."', dateUpdated='".time()."', groupID='".getLoggedGroupID()."'");

	return (get_magic_quotes_gpc() ? stripslashes($recID) : $recID);
}

function delete($selected_id, $AllowDeleteOfParents=false, $SkipChecks=false){
	// insure referential integrity ...
	global $Translation;


	// mm: can member delete record?
	$arrPerm=getTablePermissions('donations');
	$ownerGroupID=sqlValue("select groupID from membership_userrecords where tableName='donations' and pkValue='".makeSafe($selected_id)."'");
	$ownerMemberID=sqlValue("select memberID from membership_userrecords where tableName='donations' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[4]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[4]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[4]==3){ // allow delete?
		// delete allowed, so continue ...
	}else{
		return FALSE;
	}

	// child table: donations
	$res = sql("select id from donations where id='".makeSafe($selected_id)."'");
	$id = mysql_fetch_row($res);
	$rires = sql("select count(1) from donations where donation_radiotext_lookup='".addslashes($id[0])."'");
	$rirow = mysql_fetch_row($rires);
	if($rirow[0] && !$AllowDeleteOfParents && !$SkipChecks){
		$RetMsg = $Translation["couldn't delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "donations", $RetMsg);
		return $RetMsg;
	}elseif($rirow[0] && $AllowDeleteOfParents && !$SkipChecks){
		$RetMsg = $Translation["confirm delete"];
		$RetMsg = str_replace("<RelatedRecords>", $rirow[0], $RetMsg);
		$RetMsg = str_replace("<TableName>", "donations", $RetMsg);
		$RetMsg = str_replace("<Delete>", "<input type=button class=button value=\"".$Translation['yes']."\" onClick=\"window.location='donations_view.php?SelectedID=".urlencode($selected_id)."&delete_x=1&confirmed=1';\">", $RetMsg);
		$RetMsg = str_replace("<Cancel>", "<input type=button class=button value=\"".$Translation['no']."\" onClick=\"window.location='donations_view.php?SelectedID=".urlencode($selected_id)."';\">", $RetMsg);
		return $RetMsg;
	}


	// delete file stored in the 'donation_image' field
	$res = sql("select donation_image from donations where id='".makeSafe($selected_id)."'");
	if($row=@mysql_fetch_row($res)){
		if($row[0]!=''){
			@unlink(getUploadDir("").$row[0]);
		}
	}

	sql("delete from donations where id='".makeSafe($selected_id)."'");

	// mm: delete ownership data
	sql("delete from membership_userrecords where tableName='donations' and pkValue='".makeSafe($selected_id)."'");
}

function update($selected_id){
	global $HTTP_SERVER_VARS, $HTTP_GET_VARS, $HTTP_POST_VARS, $Translation;

	if($HTTP_GET_VARS['update_x']){$HTTP_POST_VARS=$HTTP_GET_VARS;}

	// mm: can member edit record?
	$arrPerm=getTablePermissions('donations');
