	$ownerMemberID=sqlValue("select memberID from membership_userrecords where tableName='donations' and pkValue='".makeSafe($selected_id)."'");
	if(($arrPerm[3]==1 && $ownerMemberID==getLoggedMemberID()) || ($arrPerm[3]==2 && $ownerGroupID==getLoggedGroupID()) || $arrPerm[3]==3){ // allow update?
		// update allowed, so continue ...
	}else{
		return;
	}

	if(get_magic_quotes_gpc()){
		$donation_soldfor = $HTTP_POST_VARS["donation_soldfor"];
		$donation_year = $HTTP_POST_VARS["donation_year"];
		$contact_id = $HTTP_POST_VARS["contact_id"];
		$contact_name_ignore = $HTTP_POST_VARS["contact_name_ignore"];
		$stockitem_id = $HTTP_POST_VARS["stockitem_id"];
		$donation_item = $HTTP_POST_VARS["donation_item"];
		$donation_radiotext_lookup = $HTTP_POST_VARS["donation_radiotext_lookup"];
		$donation_radiotext = $HTTP_POST_VARS["donation_radiotext"];
		$donation_msrp = $HTTP_POST_VARS["donation_msrp"];
		$donation_url = $HTTP_POST_VARS["donation_url"];
	}else{
		$donation_soldfor = addslashes($HTTP_POST_VARS["donation_soldfor"]);
		$donation_year = addslashes($HTTP_POST_VARS["donation_year"]);
		$contact_id = addslashes($HTTP_POST_VARS["contact_id"]);
		$contact_name_ignore = addslashes($HTTP_POST_VARS["contact_name_ignore"]);
		$stockitem_id = addslashes($HTTP_POST_VARS["stockitem_id"]);
		$donation_item = addslashes($HTTP_POST_VARS["donation_item"]);
		$donation_radiotext_lookup = addslashes($HTTP_POST_VARS["donation_radiotext_lookup"]);
		$donation_radiotext = addslashes($HTTP_POST_VARS["donation_radiotext"]);
		$donation_msrp = addslashes($HTTP_POST_VARS["donation_msrp"]);
		$donation_url = addslashes($HTTP_POST_VARS["donation_url"]);
	}

	if($HTTP_POST_VARS['donation_image_remove'] == 1){
		$donation_image = '';
		// delete file from server
		$res = sql("select donation_image from donations where id='".makeSafe($selected_id)."'");
		if($row=@mysql_fetch_row($res)){
			if($row[0]!=''){
				@unlink(getUploadDir("").$row[0]);
			}
		}
	}else{
		$donation_image = PrepareUploadedFile('donation_image', 2000000, 'jpg|jpeg|gif|png', false, "");
		// delete file from server
		if($donation_image != ''){
			$res = sql("select donation_image from donations where id='".makeSafe($selected_id)."'");
			if($row=@mysql_fetch_row($res)){
				if($row[0]!=''){
					@unlink(getUploadDir("").$row[0]);
				}
			}
		}
	}
	sql("update donations set " . "donation_year=" . (($donation_year != "") ? "'$donation_year'" : "NULL") . ", " . "contact_id=" . (($contact_id != "") ? "'$contact_id'" : "NULL") . ", " . "stockitem_id=" . (($stockitem_id != "") ? "'$stockitem_id'" : "NULL") . ", " . "donation_item=" . (($donation_item != "") ? "'$donation_item'" : "NULL") . ", " . "donation_radiotext_lookup=" . (($donation_radiotext_lookup != "") ? "'$donation_radiotext_lookup'" : "NULL") . ", " . "donation_radiotext=" . (($donation_radiotext != "") ? "'$donation_radiotext'" : "NULL") . ", " . "donation_msrp=" . (($donation_msrp != "") ? "'$donation_msrp'" : "NULL") . ", " . ($donation_image!="" ? "donation_image='$donation_image'" : ($HTTP_POST_VARS['donation_image_remove'] != 1 ? "donation_image=donation_image" : "donation_image=NULL")) . ", " . "donation_url=" . (($donation_url != "") ? "'$donation_url'" : "NULL") . " where id='".makeSafe($selected_id)."'");

	// mm: update ownership data
	sql("update membership_userrecords set dateUpdated='".time()."' where tableName='donations' and pkValue='".makeSafe($selected_id)."'");

}

