-- solicitationaids_compliance_vw		select concat(`rotarian_compliance_vw`.`llastname`,', ',`rotarian_compliance_vw`.`lfirstname`) AS `leader`,`rotarian_compliance_vw`.`rotarian_id` AS `rotarian_id`,concat(`rotarian_compliance_vw`.`rlastname`,', ',`rotarian_compliance_vw`.`rfirstname`) AS `rotarian`,`rotarian_compliance_vw`.`compliance` AS `compliance` from `rotarian_compliance_vw`;
-- solicitationaids_packet_vw		select concat(`rotarians`.`lastname`,', ',`rotarians`.`firstname`) AS `rotarian`,`rotarians`.`lead` AS `lead`,`donors`.`donor` AS `donor`,concat(' | ',`donors`.`contact1`,`donors`.`contact2`) AS `contact`,`donors`.`address` AS `address`,`donors`.`city` AS `city`,`donors`.`phone` AS `phone`,`donors`.`email` AS `email`,`donors`.`advertisement` AS `advertisement`,`items_vw`.`year` AS `year`,`items_vw`.`number` AS `number`,`items_vw`.`item` AS `item`,`items_vw`.`value` AS `value`,`items_vw`.`highbid` AS `highbid`,if(`items_vw`.`bellringer`,'(BELLRINGER)','') AS `bellringer`,if(`items_vw`.`stockitem_id`,'(STOCKITEM)','') AS `stockitem` from ((`rotarians` left join `donors` on((`rotarians`.`rotarian_id` = `donors`.`rotarian_id`))) left join `items_vw` on((`donors`.`donor_id` = `items_vw`.`donor_id`))) where (((`items_vw`.`year` between (`auction_year`() - 3) and `auction_year`()) or isnull(`items_vw`.`item_id`)) and (`donors`.`solicit` = 1));
