drop view if exists xls_insert_vw; create view xls_insert_vw as select `scheduled`.`night` AS `night`,`items`.`number` AS `number`,`items`.`item` AS `item`,`items`.`value` AS `value`,`items`.`donor` AS `donor` from (`items_current_vw` `items` left join `auctions_vw` `scheduled` on((`items`.`scheduled` = cast(`scheduled`.`start` as date)))) order by `items`.`scheduled`,`items`.`number`;
drop view if exists xls_postcards_vw; create view xls_postcards_vw as select `donors`.`donor` AS `donor`,if((`donors`.`contact1` = `donors`.`donor`),'',`donors`.`contact1`) AS `contact1`,if((`donors`.`contact2` = `donors`.`contact1`),'',`donors`.`contact2`) AS `contact2`,`donors`.`address` AS `address`,`donors`.`city` AS `city`,`donors`.`state` AS `state`,`donors`.`zip` AS `zip` from `donors_solicit_vw` `donors`;
